import React, { Component } from "react";
import RemoveTaskButton from "./../buttonComponents/RemoveTaskButton";

const style = {
  container: {
    width: "50%",
    minHeight: "40%",
    paddingTop: 30,
    paddingBottom: 30,
    paddingLeft: 15,
    paddingRight: 15,
    backgroundColor: "lightgrey",
    borderRadius: 10,
    marginTop: 15,
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "space-around",
    position: "absolute"
  },
  text: {
    margin: 0,
    padding: 0,
    fontWeight: "lighter",
    fontSize: 20,
    marginBottom: 10,
    color: '#fff'
  },
  input: {
    minWidth: 100,
    outline: "none",
    borderRadius: 5,
    border: 0,
    padding: 20
  }
};

const monthNames = [
  "January",
  "February",
  "March",
  "April",
  "May",
  "June",
  "July",
  "August",
  "September",
  "October",
  "November",
  "December"
];

function currentTime() {
  const d = new Date();
  const h = d.getHours();
  const m = d.getMinutes();
  const s = d.getSeconds();
  const day = d.getDate();
  const month = monthNames[d.getMonth()];
  const date = `${day} ${month} at ${h}:${m}:${s}`;
}

export class TaskInputField extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    const { text } = this.props;
    return (
      <div style={style.container}>
        <h6 style={style.text}>{text}</h6>
        <input style={style.input} placeholder="Task description" />
      </div>
    );
  }
}

export default TaskInputField;
