import React, { Component } from "react";
import { ReactComponent as Icon } from "./../../assets/icons/crossClose.svg";
import { getInstance } from "./../services/firebase/firebase.js";

const style = {
  container: {
    width: "50%",
    minHeight: "40%",
    paddingTop: 30,
    paddingBottom: 30,
    paddingLeft: 15,
    paddingRight: 15,
    backgroundColor: "#333333",
    opacity: 0.9,
    borderRadius: 10,
    marginTop: 15,
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "space-around",
    position: "absolute"
  },
  text: {
    margin: 0,
    padding: 0,
    fontWeight: "lighter",
    fontSize: 20,
    marginBottom: 10
  },
  input: {
    minWidth: 100,
    outline: "none",
    borderRadius: 5,
    border: 0,
    padding: 20
  },
  buttonWrapper: {
    width: 30,
    height: 30,
    backgroundColor: "#fff",
    borderRadius: 50,
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
  },
  icon: {
    width: 20,
    height: 20
  }
};

const monthNames = [
  "January",
  "February",
  "March",
  "April",
  "May",
  "June",
  "July",
  "August",
  "September",
  "October",
  "November",
  "December"
];

const firebase = getInstance()
const db = firebase.database()
console.log(db)

currentTime = () => {
  const d = new Date();
  const h = d.getHours();
  const m = d.getMinutes();
  const s = d.getSeconds();
  const day = d.getDate();
  const month = monthNames[d.getMonth()];
  const date = `${day} ${month} at ${h}:${m}:${s}`;
}

export class TaskInputField extends Component {
  state = {
    showInputScreen: "false"
  };

  constructor(props) {
    super(props);
  }

  handleClick = () => {
    const componentState =
      this.state.showInputScreen === "flex" ? "none" : "flex";

    this.setState({
      showInputScreen: componentState
    });
  };
  render() {
    const { text } = this.props;
    return (
      <div style={style.container}>
        <div style={style.buttonWrapper} onClick={this.handleClick}>
          <Icon style={style.icon} />
        </div>
        <h6 style={style.text}>{text}</h6>
        <input style={style.input} placeholder="Task description" />
      </div>
    );
  }
}

export default TaskInputField;
